// src/components/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [pendingClubs, setPendingClubs] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const resClubs = await axios.get('/admin/clubs/pending', {
          headers: { Authorization: localStorage.getItem('token') },
        });
        const resUsers = await axios.get('/admin/users', {
          headers: { Authorization: localStorage.getItem('token') },
        });
        setPendingClubs(resClubs.data);
        setUsers(resUsers.data);
      } catch (err) {
        console.error("Error fetching data", err);
      }
    };
    fetchData();
  }, []);

  const approveClub = async (clubId) => {
    try {
      await axios.put(`/admin/clubs/${clubId}/approve`, {}, {
        headers: { Authorization: localStorage.getItem('token') },
      });

      setPendingClubs((prevClubs) => prevClubs.filter(club => club.id !== clubId));

      // Refetch users after approval to include the newly approved club
      const resUsers = await axios.get('/admin/users', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setUsers(resUsers.data);
    } catch (err) {
      alert(err.response.data.error || "Error approving club");
    }
  };

  const deleteUser = async (userId) => {
    try {
      await axios.delete(`/admin/users/${userId}`, {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setUsers((prevUsers) => prevUsers.filter(user => user.id !== userId));
    } catch (err) {
      alert(err.response.data.error || "Error deleting user");
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Admin Dashboard</h2>

      <div style={styles.section}>
        <h3 style={styles.sectionTitle}>Pending Clubs</h3>
        <ul style={styles.list}>
          {pendingClubs.map((club) => (
            <li key={club.id} style={styles.card}>
              <div style={styles.clubDetails}>
                <p style={styles.itemName}><strong>{club.name}</strong></p>
                <p style={styles.itemDetail}><strong>Description:</strong> {club.description}</p>
                <p style={styles.itemDetail}><strong>Contact Email:</strong> {club.contact_email}</p>
              </div>
              <button style={styles.approveButton} onClick={() => approveClub(club.id)}>
                Approve
              </button>
            </li>
          ))}
        </ul>
      </div>

      <div style={styles.section}>
        <h3 style={styles.sectionTitle}>Manage Users</h3>
        <ul style={styles.list}>
          {users.map((user) => (
            <li key={user.id} style={styles.card}>
              <div style={styles.userDetails}>
                <span style={styles.itemName}>
                  {user.username} <span style={styles.userRole}>({user.role})</span>
                </span>
                {user.role === 'club' && (
                  <>
                    <p style={styles.itemDetail}><strong>Description:</strong> {user.description}</p>
                    <p style={styles.itemDetail}><strong>Contact Email:</strong> {user.contact_email}</p>
                  </>
                )}
              </div>
              <button style={styles.deleteButton} onClick={() => deleteUser(user.id)}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    textAlign: 'center',
    fontSize: '28px',
    marginBottom: '20px',
  },
  section: {
    marginBottom: '30px',
  },
  sectionTitle: {
    fontSize: '22px',
    marginBottom: '10px',
    color: '#333',
    borderBottom: '2px solid #4CAF50',
    paddingBottom: '5px',
  },
  list: {
    listStyleType: 'none',
    padding: 0,
  },
  card: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px',
    margin: '10px 0',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    backgroundColor: '#fff',
  },
  clubDetails: {
    textAlign: 'left',
    maxWidth: '70%',
  },
  userDetails: {
    textAlign: 'left',
    maxWidth: '70%',
  },
  itemName: {
    fontSize: '18px',
    color: '#333',
  },
  itemDetail: {
    fontSize: '14px',
    color: '#555',
    marginTop: '4px',
  },
  userRole: {
    fontSize: '14px',
    color: '#777',
    marginLeft: '5px',
  },
  approveButton: {
    backgroundColor: '#4CAF50',
    color: '#fff',
    padding: '8px 16px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontWeight: 'bold',
  },
  deleteButton: {
    backgroundColor: '#f44336',
    color: '#fff',
    padding: '8px 16px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontWeight: 'bold',
  },
};

export default AdminDashboard;
