// src/components/StudentDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function StudentDashboard() {
  const [clubs, setClubs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClubs = async () => {
      const res = await axios.get('/student/clubs', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setClubs(res.data);
    };
    fetchClubs();
  }, []);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredClubs = clubs.filter((club) =>
    club.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h2>Available Clubs</h2>
      <input
        type="text"
        placeholder="Search for clubs..."
        value={searchQuery}
        onChange={handleSearch}
      />
      <button onClick={() => navigate('/student/orders')}>Your Orders</button>
      <div className="club-list">
        {filteredClubs.map((club) => (
          <div key={club.id} className="club-card" onClick={() => navigate(`/student/clubs/${club.id}/services`)}>
            <img src={club.image_url || '/placeholder.jpg'} alt={club.name} className="club-image" />
            <h3>{club.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StudentDashboard;
