// src/components/OrderPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function OrderPage() {
  const { serviceId } = useParams();
  const [service, setService] = useState({});
  const [quantity, setQuantity] = useState(1);
  const [location, setLocation] = useState('');
  const [additionalInfo, setAdditionalInfo] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchService = async () => {
      const res = await axios.get(`/student/services/${serviceId}`, {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setService(res.data);
    };
    fetchService();
  }, [serviceId]);

  const handleOrder = async () => {
    try {
      await axios.post(
        '/student/orders',
        { serviceId: service.id, quantity, location, additionalInfo },
        { headers: { Authorization: localStorage.getItem('token') } }
      );
      alert('Thank you for your order!');
      setTimeout(() => navigate('/student/orders'), 5000);
    } catch (err) {
      alert(err.response.data.error);
    }
  };

  return (
    <div>
      <h2>Order {service.name}</h2>
      <p>{service.description}</p>
      <input
        type="number"
        value={quantity}
        min="1"
        onChange={(e) => setQuantity(e.target.value)}
        placeholder="Quantity"
      />
      <input
        type="text"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        placeholder="Location"
      />
      <textarea
        value={additionalInfo}
        onChange={(e) => setAdditionalInfo(e.target.value)}
        placeholder="Additional Information"
      />
      <button onClick={handleOrder}>Submit Order</button>
    </div>
  );
}

export default OrderPage;
