// src/components/StudentDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function StudentDashboard() {
  const [clubs, setClubs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [username, setUsername] = useState('User'); // Placeholder; replace with actual username fetch if available
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClubs = async () => {
      const res = await axios.get('/student/clubs', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setClubs(res.data);
    };

    // Assuming you have an endpoint to get the user's profile data
    const fetchUserData = async () => {
      const res = await axios.get('/student/profile', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setUsername(res.data.username);
    };

    fetchClubs();
    fetchUserData();
  }, []);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredClubs = clubs.filter((club) =>
    club.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.welcome}>Welcome, {username}</div>
        <button style={styles.orderButton} onClick={() => navigate('/student/orders')}>
          Your Orders
        </button>
      </div>
      <input
        type="text"
        placeholder="Search for clubs..."
        value={searchQuery}
        onChange={handleSearch}
        style={styles.searchBar}
      />
      <div style={styles.clubList}>
        {filteredClubs.map((club) => (
          <div
            key={club.id}
            style={styles.clubCard}
            onClick={() => navigate(`/student/clubs/${club.id}/services`)}
          >
            <img src={club.image_url || '/placeholder.jpg'} alt={club.name} style={styles.clubImage} />
            <h3 style={styles.clubName}>{club.name}</h3>
          </div>
        ))}
      </div>
      <button style={styles.logoutButton} onClick={handleLogout}>
        Logout
      </button>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#f4f4f9',
    minHeight: '100vh',
    position: 'relative',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '20px',
  },
  welcome: {
    fontSize: '20px',
    fontWeight: 'bold',
    color: '#333',
  },
  orderButton: {
    padding: '8px 15px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  searchBar: {
    width: '100%',
    padding: '10px',
    margin: '20px 0',
    fontSize: '16px',
    borderRadius: '4px',
    border: '1px solid #ddd',
  },
  clubList: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '15px',
    justifyContent: 'center',
  },
  clubCard: {
    width: '150px',
    padding: '15px',
    textAlign: 'center',
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    cursor: 'pointer',
    transition: 'transform 0.2s',
  },
  clubCardHover: {
    transform: 'scale(1.05)',
  },
  clubImage: {
    width: '100%',
    height: '100px',
    objectFit: 'cover',
    borderRadius: '4px',
  },
  clubName: {
    fontSize: '18px',
    color: '#333',
    marginTop: '10px',
  },
  logoutButton: {
    position: 'absolute',
    bottom: '20px',
    left: '20px',
    padding: '8px 15px',
    fontSize: '16px',
    backgroundColor: '#ff6347',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default StudentDashboard;
