// src/components/StudentDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function StudentDashboard() {
  const [clubs, setClubs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const studentName = "John Doe"; // Placeholder for student's name; replace with dynamic data if available
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClubs = async () => {
      const res = await axios.get('/student/clubs', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setClubs(res.data);
    };
    fetchClubs();
  }, []);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const filteredClubs = clubs.filter((club) =>
    club.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3 style={styles.welcomeText}>Welcome, {studentName}</h3>
        <button style={styles.orderButton} onClick={() => navigate('/student/orders')}>
          Your Orders
        </button>
      </div>
      <input
        type="text"
        placeholder="Search for clubs..."
        value={searchQuery}
        onChange={handleSearch}
        style={styles.searchBar}
      />
      <div style={styles.clubList}>
        {filteredClubs.map((club) => (
          <div
            key={club.id}
            style={styles.clubCard}
            onClick={() => navigate(`/student/clubs/${club.id}/services`)}
          >
            <img src={club.image_url || '/placeholder.jpg'} alt={club.name} style={styles.clubImage} />
            <h3 style={styles.clubName}>{club.name}</h3>
          </div>
        ))}
      </div>
      <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
    </div>
  );
}

const styles = {
  container: { padding: '20px', position: 'relative', minHeight: '100vh', backgroundColor: '#f9f9f9' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' },
  welcomeText: { fontSize: '20px', color: '#333' },
  orderButton: { padding: '10px 20px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' },
  searchBar: { width: '100%', padding: '10px', marginBottom: '20px', fontSize: '16px', borderRadius: '4px', border: '1px solid #ddd' },
  clubList: { display: 'flex', flexWrap: 'wrap', gap: '15px', justifyContent: 'center' },
  clubCard: { width: '150px', padding: '10px', textAlign: 'center', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', cursor: 'pointer', borderRadius: '8px', backgroundColor: '#fff' },
  clubImage: { width: '100%', height: '100px', objectFit: 'cover', borderRadius: '4px' },
  clubName: { fontSize: '18px', margin: '10px 0', color: '#333' },
  logoutButton: { position: 'absolute', bottom: '20px', left: '20px', padding: '10px 20px', backgroundColor: '#d9534f', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' },
};

export default StudentDashboard;
