// src/components/RegisterClub.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function RegisterClub() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',       // Match this with the backend field for username
    email: '',
    password: '',
    clubName: '',       // Additional club-specific field
    description: ''     // Additional club-specific field
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send the formData to the backend for club registration
      const response = await axios.post('/auth/register/club', formData);
      alert(response.data.message || 'Club registration successful! Await admin approval.');
      navigate('/login');
    } catch (err) {
      // Display error message, handling cases where response might be undefined
      const errorMsg = err.response?.data?.error || 'An error occurred during registration';
      alert(errorMsg);
    }
  };

  return (
    <div>
      <h2>Club Registration</h2>
      <form onSubmit={handleSubmit}>
       
        <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required />
        <input name="clubName" placeholder="Club Name" onChange={handleChange} required />
        <textarea name="description" placeholder="Club Description" onChange={handleChange} />
        <button type="submit">Sign Up</button>
      </form>
      <p>
        Already have an account? <a href="/login">Log in</a>
      </p>
    </div>
  );
}

export default RegisterClub;
