// src/components/RegisterClub.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function RegisterClub() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    clubName: '',
    contactEmail: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Log formData to ensure all fields are present
    console.log('Submitting form with data:', formData);

    try {
      // Ensure all required fields are provided
      if (!formData.username || !formData.password || !formData.clubName || !formData.contactEmail) {
        alert('All required fields (username, password, club name, contact email) must be filled out.');
        return;
      }

      await axios.post('/auth/register/club', formData);
      alert('Club registration successful! Await admin approval.');
      navigate('/login');
    } catch (err) {
      alert(err.response?.data?.error || 'An error occurred');
    }
  };

  return (
    <div>
      <h2>Club Registration</h2>
      <form onSubmit={handleSubmit}>
        <input name="username" placeholder="Username" onChange={handleChange} required />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required />
        <input name="clubName" placeholder="Club Name" onChange={handleChange} required />
        <input name="contactEmail" type="email" placeholder="Contact Email" onChange={handleChange} required />
        <textarea name="description" placeholder="Club Description" onChange={handleChange} />
        <button type="submit">Sign Up</button>
      </form>
      <p>
        Already have an account? <a href="/login">Log in</a>
      </p>
    </div>
  );
}

export default RegisterClub;
