// src/components/OrderHistory.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function OrderHistory() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get('/student/orders', {
          headers: { Authorization: localStorage.getItem('token') },
        });
        setOrders(res.data);
      } catch (err) {
        alert('Error fetching order history');
      }
    };
    fetchOrders();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Your Order History</h2>
      <div style={styles.orderList}>
        {orders.map((order) => (
          <div key={order.id} style={styles.orderCard}>
            <p><strong>Service:</strong> {order.service_name}</p>
            <p><strong>Club:</strong> {order.club_name}</p>
            <p><strong>Quantity:</strong> {order.quantity}</p>
            <p><strong>Status:</strong> {order.status}</p>
            <p><strong>Location:</strong> {order.location}</p>
            <p><strong>Additional Info:</strong> {order.additional_info}</p>
            <p><strong>Date:</strong> {new Date(order.created_at).toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    fontSize: '24px',
    marginBottom: '20px',
    textAlign: 'center',
    color: '#333',
  },
  orderList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
    alignItems: 'center',
  },
  orderCard: {
    width: '90%',
    maxWidth: '600px',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    backgroundColor: '#f9f9f9',
  },
  label: {
    fontWeight: 'bold',
  },
};

export default OrderHistory;
